    typedef enum reg [1:0] {
        IDLE   = 2'b00,
        WAIT   = 2'b01,
        PROCESS= 2'b10,
        DONE   = 2'b11
    } state_t;

    state_t state, next_state;
    reg [3:0] counter;
    parameter THRESHOLD = 4'hA; // Example threshold

    always @(posedge clock or posedge reset) begin
        if (reset) begin
            state <= IDLE;
            counter <= 0;
            data_out <= 0;
            valid <= 0;
        end else begin
            state <= next_state;
        end
    end

    always @(state or counter) begin
        case (state)
            IDLE: begin
                next_state = WAIT;
                counter = 0;
                data_out = 0;
                valid = 0;
            end

            WAIT: begin
                if (counter < THRESHOLD) begin
                    counter = counter + 1;
                    next_state = WAIT;
                end else begin
                    next_state = PROCESS;
                end
                data_out = 0;
                valid = 0;
            end

            PROCESS: begin
                data_out = 8'hFF; // Example data processing
                next_state = DONE;
                valid = 0;
            end

            DONE: begin
                valid = 1;
                next_state = IDLE;
                data_out = data_out; // Hold the processed data
            end
        endcase
    end
endmodule
