// 50ns            0               0               6b              1               2c8109          
// 55ns            1               0                d              0                    x          
// 60ns            0               0                d              0                    x          
// 65ns            1               0               8d              0                    x          
// 70ns            0               0               8d              0                    x          
// 75ns            1               0               6d              1               6b0d8d          
// 80ns            0               0               6d              1               6b0d8d          
// 85ns            1               0               12              0                    x          
// 90ns            0               0               12              0                    x          
// 95ns            1               0                1              0                    x          
// 100ns           0               0                1              0                    x          
// 105ns           1               0                d              1               6d1201          
// 110ns           0               0                d              1               6d1201          
// 115ns           1               0               76              0                    x          
// 120ns           0               0               76              0                    x          
// 125ns           1               0               3d              0                    x          
// 130ns           0               0               3d              0                    x          
// 135ns           1               0               ed              1                d763d          
// 140ns           0               0               ed              1                d763d          
// 145ns           1               0               8c              0                    x          
// 150ns           0               0               8c              0                    x          
// 155ns           1               0               f9              0                    x          
// 160ns           0               0               f9              0                    x          
// 165ns           1               0               ce              1               ed8cf9          
// 170ns           0               0               ce              1               ed8cf9          
// 175ns           1               0               c5              0                    x          
// 180ns           0               0               c5              0                    x          
// 185ns           1               0               aa              0                    x          
// 190ns           0               0               aa              0                    x

completion:
reg [2:0] state;
reg [23:0] out_bytes_r;
reg done_r;

parameter IDLE = 3'b000;
parameter BYTE1 = 3'b001;
parameter BYTE2 = 3'b010;
parameter BYTE3 = 3'b011;

always @(posedge clk) begin
    if (reset) begin
        state <= IDLE;
        out_bytes_r <= 0;
		  done_r <= 0;
    end else begin
        case (state)
            IDLE: begin
					 done_r <= 0;
                if (in[3]) begin
                    out_bytes_r[23:16] <= in;
                    state <= BYTE1;
                end
            end
            BYTE1: begin
                out_bytes_r[15:8] <= in;
                state <= BYTE2;
            end
            BYTE2: begin
                out_bytes_r[7:0] <= in;
                state <= BYTE3;
            end
            BYTE3: begin
				    done_r <= 1;
                state <= IDLE;
            end
            default: begin
                state <= IDLE;
					 done_r <= 0;
            end
        endcase
    end
end

assign out_bytes = out_bytes_r;
assign done = done_r;

endmodule

