    parameter S0 = 4'b0000;
    parameter S1 = 4'b0001;
    parameter S2 = 4'b0010;
    parameter S3 = 4'b0011;
    parameter S4 = 4'b0100;
    parameter S5 = 4'b0101;
    parameter S6 = 4'b0110;
    parameter S7 = 4'b0111;
    parameter S8 = 4'b1000;

    always @(posedge clk) begin
        if (reset) begin
            state <= S0;
            disc_r <= 0;
            flag_r <= 0;
            err_r <= 0;
        end else begin
            disc_r <= 0;
            flag_r <= 0;
            err_r <= 0;

            case (state)
                S0: begin
                    if (in) state <= S1;
                    else state <= S0;
                end
                S1: begin
                    if (in) state <= S2;
                    else state <= S0;
                end
                S2: begin
                    if (in) state <= S3;
                    else state <= S0;
                end
                S3: begin
                    if (in) state <= S4;
                    else state <= S0;
                end
                S4: begin
                    if (in) state <= S5;
                    else state <= S0;
                end
                S5: begin
                    if (in) state <= S6;
                    else state <= S0;
                end
                S6: begin
                    if (in) state <= S7;
                    else begin
                        state <= S0;
                        disc_r <= 1;
                    end
                end
                S7: begin
                    if (in) begin
                         state <= S8;
                         err_r <= 1;
                    end
                    else begin
                         state <= S0;
                         flag_r <= 1;
                    end
                end
                S8: begin
