                        state <= GET_DELAY;
                        counting <= 0;
                        done <= 0;
                    end else begin
                        state <= IDLE;
                    end
                end
                GET_DELAY: begin
                    delay <= {data, delay[3:1]};
                    if (counter == 4'd3) begin
