        WALK_RIGHT: begin
          if (!ground) begin
            state <= FALL;
            fall_count <= 0;
          end else if (dig) begin
            state <= DIG;
          end else if (bump_left || bump_right) begin
            state <= WALK_LEFT;
          end else begin
            state <= WALK_RIGHT;
          end
        end
        FALL: begin
          if (ground) begin
            if (fall_count > 20) begin
              state <= SPLAT;
            end else begin
              if (walk_left) begin
                state <= WALK_LEFT;
              end else begin
                state <= WALK_RIGHT;
              end
            end
            fall_count <= 0;
          end else begin
             state <= FALL;
             fall_count <= fall_count + 1;
          end
        end
        DIG: begin
          if (!ground) begin
            state <= FALL;
            fall_count <= 0;
          end else begin
            state <= DIG;
          end
        end
        SPLAT: begin
          state <= SPLAT;
        end
        default: begin
          state <= WALK_LEFT;
          fall_count <= 0;
        end
      endcase
