    if (load) begin
        q <= data;
    end else begin
        reg [511:0] next_q;
        integer i;
        for (i = 0; i < 512; i = i + 1) begin
            // Rule 110
            if (i == 0) begin
                next_q[i] <= (q[i] && !q[i+1]) || (!q[i] && q[i+1]) || (!q[i] && !q[i+1]);
            end else if (i == 511) begin
