parameter S111 = 4'b0011;
parameter S1111 = 4'b0100;
parameter S11111 = 4'b0101;
parameter S111110 = 4'b0110;
parameter S111111 = 4'b0111;
parameter S1111110 = 4'b1000;
parameter S1111111X = 4'b1001;


