always @(posedge clk) begin
    if (reset) begin
        state <= S0;
		  disc_r <= 0;
		  flag_r <= 0;
		  err_r <= 0;
    end else begin
		disc_r <= 0;
		flag_r <= 0;
		err_r <= 0;
        case (state)
            S0: begin
                if (in) state <= S1;
                else state <= S0;
            end
            S1: begin
                if (in) state <= S11;
                else state <= S0;
            end
            S11: begin
                if (in) state <= S111;
