    reg [3:0] state;
    reg [11:0] cycle_count;

    localparam IDLE = 0;
    localparam S1 = 1;
    localparam S2 = 2;
    localparam S3 = 3;
    localparam GET_DELAY = 4;
    localparam COUNTING = 5;
    localparam DONE = 6;

    assign count = (state == COUNTING) ? delay : 4'bx;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            counting <= 0;
            done <= 0;
            delay <= 0;
            cycle_count <= 0;
        end else begin
            case (state)
                IDLE: begin
                    if (data) begin
                        state <= S1;
                    end
                end
                S1: begin
                    if (data) begin
                        state <= S2;
                    end else begin
                        state <= IDLE;
                    end
                end
                S2: begin
                    if (~data) begin
                        state <= S3;
                    end else begin
                        state <= IDLE;
                    end
                end
                S3: begin
                    if (data) begin
                        state <= GET_DELAY;
                    end else begin
                        state <= IDLE;
                    end
                end
                GET_DELAY: begin
                    delay[3] <= data;
                    state <= GET_DELAY + 1;
                end
                GET_DELAY + 1: begin
                    delay[2] <= data;
                    state <= GET_DELAY + 2;
                end
                GET_DELAY + 2: begin
                    delay[1] <= data;
                    state <= GET_DELAY + 3;
                end
                GET_DELAY + 3: begin
                    delay[0] <= data;
                    state <= COUNTING;
                    counting <= 1;
                    cycle_count <= 0;
                end
                COUNTING: begin
                    if (cycle_count == 999) begin
                        cycle_count <= 0;
                        if (delay == 0) begin
                            state <= DONE;
                            counting <= 0;
                            done <= 1;
                        end else begin
                            delay <= delay - 1;
                        end
                    end else begin
                        cycle_count <= cycle_count + 1;
                    end
                end
                DONE: begin
                    if (ack) begin
                        state <= IDLE;
                        done <= 0;
                    end
                end
                default: begin
                    state <= IDLE;
                end
            endcase
        end
    end
endmodule

