              is_walking_left <= 1'b0;
              is_falling <= 1'b0;
              is_digging <= 1'b0;
            end else begin
              is_falling <= 1'b0;
            end
          end else begin
            fall_counter <= fall_counter + 1'b1;
          end
        end else if (is_digging) begin
          if (ground) begin
            is_digging <= 1'b1;
          end else begin
            is_digging <= 1'b0;
            is_falling <= 1'b1;
            fall_counter <= 6'b0;
          end
        end else begin
          if (!ground) begin
            is_falling <= 1'b1;
            fall_counter <= 6'b0;
          end else if (dig) begin
