            reg [511:0] next_q;
            for (int i = 0; i < 512; i++) begin
                // Determine the neighborhood
                logic left   = (i == 0)   ? 0 : q[i-1];
                logic center = q[i];
                logic right  = (i == 511) ? 0 : q[i+1];

                // Apply Rule 110
                if      (left && center && right) next_q[i] = 0;
                else if (left && center && !right) next_q[i] = 1;
                else if (left && !center && right) next_q[i] = 1;
                else if (left && !center && !right) next_q[i] = 0;
                else if (!left && center && right) next_q[i] = 1;
                else if (!left && center && !right) next_q[i] = 1;
                else if (!left && !center && right) next_q[i] = 1;
                else                                next_q[i] = 0;
