description:
The following is the state transition table for a Moore state machine with one input, one output, and four states. Implement this state machine. Include a positive edge triggered asynchronous reset that resets the FSM to state A.

// state | next state in=0, next state in=1 | output
// A | A, B | 0
// B | C, B | 0
// C | A, D | 0
// D | C, B | 1

prompt:
module top_module (
	input clk,
	input in,
	input areset,
	output out
);

completion:
	reg [1:0] state;
	reg [1:0] next_state;
	reg out_reg;

	always @(posedge clk or posedge areset) begin
		if (areset) begin
			state <= 2'b00;
			next_state <= 2'b00;
			out_reg <= 1'b0;
		end else begin
			state <= next_state;
			out_reg <= out_reg;
			case (state)
				2'b00: begin
					next_state = in ? 2'b01 : 2'b00;
					out_reg = 1'b0;
				end
				2'b01: begin
					next_state = in ? 2'b10 : 2'b01;
					out_reg = 1'b0;
				end
				2'b10: begin
					next_state = in ? 2'b00 : 2'b10;
					out_reg = 1'b0;
				end
				2'b11: begin
					next_state = in ? 2'b01 : 2'b11;
					out_reg = 1'b1;
				end
			endcase
		end
	end

	assign out = out_reg;


endmodule

