reg [23:0] message;

always @(posedge clk) begin
	if (reset) begin
		state <= 0;
		message <= 0;
	end else begin
		case (state)
			0: begin
				if (in[3] == 1) begin
					message[23:16] <= in;
					state <= 1;
				end
			end
			1: begin
