					g[3] = 0;
				end
			end
			C: begin
				if (r[2]) begin
					next_state = C;
					g[1] = 0;
					g[2] = 1;
					g[3] = 0;
				end else begin
					next_state = A;
					g[1] = 0;
					g[2] = 0;
					g[3] = 0;
				end
			end
			D: begin
				if (r[3]) begin
					next_state = D;
					g[1] = 0;
					g[2] = 0;
					g[3] = 1;
				end else begin
					next_state = A;
					g[1] = 0;
					g[2] = 0;
					g[3] = 0;
