	reg [2:0] next_state;

	always @(posedge clk) begin
		if (reset) begin
			state <= 3'b000;
		end else begin
			state <= next_state;
		end
	end

	always @(*) begin
		case (state)
			3'b000: begin
				if (in == 1'b0) begin
					next_state = 3'b001;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b001: begin
				if (in == 1'b1) begin
					next_state = 3'b010;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b010: begin
				if (in == 1'b1) begin
					next_state = 3'b011;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b011: begin
				if (in == 1'b1) begin
					next_state = 3'b100;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b100: begin
				if (in == 1'b1) begin
					next_state = 3'b101;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b101: begin
				if (in == 1'b1) begin
					next_state = 3'b110;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b110: begin
				if (in == 1'b1) begin
					next_state = 3'b111;
				end else begin
					next_state = 3'b000;
				end
			end
			3'b111: begin
				if (in == 1'b1) begin
					next_state = 3'b111;
				end else begin
					next_state = 3'b000;
				end
			end
		endcase
	end

