module top_module (
	input clk,
	input in,
	input reset,
	output done
);

completion:
	parameter IDLE = 2'b00;
	parameter START = 2'b01;
	parameter DATA = 2'b10;
	parameter STOP = 2'b11;

	reg [1:0] state;
	reg [7:0] data;
	reg [2:0] bit_count;

	always @(posedge clk) begin
		if (reset) begin
			state <= IDLE;
			data <= 8'b0;
			bit_count <= 3'b0;
			done <= 1'b0;
		end else begin
			case (state)
				IDLE: begin
					if (in == 1'b0) begin
						state <= START;
						bit_count <= 3'b0;
					end
				end
				START: begin
					if (bit_count == 3'b0) begin
						state <= DATA;
					end else begin
						data <= {in, data[7:1]};
						bit_count <= bit_count + 1;
					end
				end
				DATA: begin
					if (bit_count == 3'b7) begin
						state <= STOP;
					end else begin
						data <= {in, data[7:1]};
						bit_count <= bit_count + 1;
					end
				end
				STOP: begin
					if (in == 1'b1) begin
						state <= IDLE;
						done <= 1'b1;
					end else begin
						state <= START;
						bit_count <= 3'b0;
					end
				end
			endcase
		end
	end


endmodule

reg [3:0] state;
