	parameter B = 2'b01;
	parameter C = 2'b10;
	parameter D = 2'b11;

	// Define the state flip-flops
	reg [1:0] state;
	reg [1:0] next_state;

	// Define the state table
	always @ (posedge clk or negedge resetn) begin
		if (~resetn) begin
			state <= A;
		end else begin
			state <= next_state;
		end
	end

	// Define the output logic
	always @ (state, r) begin
		case (state)
			A: begin
				if (r[1] && ~r[2] && ~r[3]) begin
					next_state = B;
					g[1] = 1;
					g[2] = 0;
					g[3] = 0;
				end else if (~r[1] && r[2] && ~r[3]) begin
					next_state = C;
					g[1] = 0;
					g[2] = 1;
					g[3] = 0;
