			state <= 3'b000;
			z <= 1'b0;
		end
		else begin
			state <= next_state;
			case (state)
				3'b000: begin
					if (x) next_state <= 3'b001;
					else next_state <= 3'b000;
					z <= 1'b0;
				end
				3'b001: begin
					if (x) next_state <= 3'b100;
					else next_state <= 3'b001;
					z <= 1'b0;
				end
				3'b010: begin
					if (x) next_state <= 3'b001;
					else next_state <= 3'b010;
					z <= 1'b0;
				end
				3'b011: begin
					if (x) next_state <= 3'b100;
					else next_state <= 3'b010;
					z <= 1'b1;
				end
				3'b100: begin
					if (x) next_state <= 3'b100;
					else next_state <= 3'b011;
					z <= 1'b1;
				end
				default: begin
					next_state <= 3'b000;
					z <= 1'b0;
				end
			endcase
		end
	end


endmodule

