		.D(x),
		.CLK(clk),
		.Q(q1),
		.QN(not_q1)
	);

	DFF dff2 (
		.D(xor_out),
		.CLK(clk),
		.Q(q2),
		.QN(not_q2)
	);

	DFF dff3 (
		.D(or_out),
		.CLK(clk),
		.Q(q3),
		.QN(not_q3)
	);

