    parameter IDLE = 2'b00;
    parameter SHIFT = 2'b01;
    parameter WAIT = 2'b10;
    parameter NOTIFY = 2'b11;

    reg [1:0] state;
    reg [3:0] shift_reg;
    reg [3:0] duration;

    always @(posedge clk) begin
        if (reset) begin
            state <= IDLE;
            shift_reg <= 4'b0000;
            duration <= 4'b0000;
            shift_ena <= 1'b0;
            counting <= 1'b0;
            done <= 1'b0;
        end else begin
            case (state)
                IDLE: begin
                    if (shift_reg == 4'b1101) begin
                        state <= SHIFT;
                        shift_ena <= 1'b1;
                    end else begin
                        shift_reg <= {shift_reg[2:0], data};
                    end
                end
                SHIFT: begin
                    if (shift_ena) begin
                        duration <= {duration[2:0], data};
                        shift_ena <= 1'b0;
                        state <= WAIT;
                        counting <= 1'b1;
                    end
                end
                WAIT: begin
                    if (done_counting) begin
                        state <= NOTIFY;
                        done <= 1'b1;
                    end
                end
                NOTIFY: begin
                    if (ack) begin
                        state <= IDLE;
                        done <= 1'b0;
                    end
                end
            endcase
        end
    end


