	parameter IDLE = 2'b00;
	parameter SHIFT = 2'b01;
	parameter COMP = 2'b10;

	always @(posedge clk or negedge areset) begin
		if (!areset) begin
			shift_reg <= 0;
			twos_comp <= 0;
			state <= IDLE;
		end else begin
			case (state)
				IDLE: begin
					shift_reg <= 0;
					twos_comp <= 0;
