	reg [1:0] next_state;
	reg out_reg;

	always @(posedge clk or posedge areset) begin
		if (areset) begin
			state <= 2'b00;
			next_state <= 2'b00;
			out_reg <= 1'b0;
		end else begin
			state <= next_state;
			out_reg <= (state == 2'b11) ? 1'b1 : 1'b0;
		end
	end

	always @(*) begin
		case (state)
			2'b00: begin
				next_state = in ? 2'b01 : 2'b00;
			end
			2'b01: begin
				next_state = in ? 2'b10 : 2'b01;
			end
			2'b10: begin
				next_state = in ? 2'b00 : 2'b10;
			end
			2'b11: begin
				next_state = in ? 2'b11 : 2'b10;
			end
		endcase
	end

	assign out = out_reg;


